
public class Star2patern {

	/**
	 * @param args
	 */
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
			
			//int term=6;  
			for(int i=6;i>=1;i--)
			{  
			for(int j=1;j<=i;j++)
			{  
			       System.out.print("* ");  
			}  
			System.out.println();//new line  
			}  
		}  
		
}
